function ravMilimParser(){copyToClipboard(window.getSelection().toString().trim().replace(/â€¢\s/g,"    - "))}function hebrewAcademyParser(){copyToClipboard(window.getSelection().toString().trim().split("\n").map(function(e,o){return o+1+". "+e.trim()}).join("\n"))}function milogParser(){var e=document.getSelection().getRangeAt(0).cloneContents().querySelectorAll(".ent_para"),o="";e.forEach((e,t)=>{o+=`${t+1}. ${e.querySelector(".ent_para_text").textContent.trim()}
`}),copyToClipboard(o)}function wiktionaryParser(){var e=document.getSelection().getRangeAt(0).cloneContents().querySelectorAll("li"),o="",t=1;e.forEach(function(e){if(!e.closest("ul")){var r=e.cloneNode(!0),n=r.querySelector("ul");n&&n.remove(),o+=t+". "+r.textContent.trim()+"\n",e.querySelectorAll("ul > li").forEach(function(e){o+="    - "+e.textContent.trim()+"\n"}),t++}}),copyToClipboard(o)}function copyToClipboard(e){console.log(e),navigator.clipboard.writeText(e)}document.addEventListener("copy",function(e){if(console.log("copy triggered!"),"www.ravmilim.co.il"===window.location.hostname)ravMilimParser();else if("hebrew-academy.org.il"===window.location.hostname)hebrewAcademyParser();else if("milog.co.il"===window.location.hostname)milogParser();else if("he.wiktionary.org"===window.location.hostname)wiktionaryParser();else{console.log("not a valid host");return}e.preventDefault()});